package class23;

public abstract class File {/*Create a class File that will have the following behaviors: open, edit, close. Edit and close are implemented
 method while open is an abstract. Create 3 subclasses: JavaFile, WordFile, PdfFile that will provide specific implementation of open behaviour:
 Example: to open .java file we need notepad++ or sublime text, to open .doc file we need Microsoft word to be installed etc*/

    abstract void open();

    void edit() {
        System.out.println("Files are can be editable");
    }

    void close() {
        System.out.println("We can close the files whenever we need to close them");
    }
}class JavaFile extends File{
    @Override
    void open() {
        System.out.println("To open java file you need to download and setup the Java first");

    }
}class WordFile extends File{void open() {
    System.out.println("To open Word File you need to download and setup the Microsoft Word first");

}}class PdfFile extends File{
    @Override
    void open() {
        System.out.println("To open PDF File you need to download and setup the PDF first");

    }


    public static void main(String[] args) {
        File []f={new JavaFile(),new WordFile(),new PdfFile()};
        for(File x:f){
            x.open();
            x.edit();
            x.close();
            System.out.println("------------------------------------------------------------------                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ");
            for (int i = 0; i <f.length ; i++) {
                File o=f[i];
                o.open();
                o.edit();
                o.close();

            }
        }
    }
}
